mapper.delete:
DELETE FROM b_todo
WHERE b_todo.state=1 AND b_todo.id IN

  #{id}

mapper.select:
SELECT *
FROM b_todo


    and id = #{id}


    and category_id = #{categoryId}


    and user_id = #{userId}


    and priority = #{priority}


    and state = #{state}


    and TIMEDIFF(#{startTime}, start_time) <= 0


    and TIMEDIFF(#{endTime}, start_time) >= 0
